sys_script.script:
(function executeRule(current, previous) {
    var bidAmount = parseFloat(current.getValue('bid_amount'));
    var itemName = current.getValue('item_name');


    var util = new BidValidationUtil();
    var validationResult = util.validateBidAmount(itemName, bidAmount);

    if (validationResult === 'error') {
        gs.addErrorMessage('Bid amount must be greater than the current highest bid amount.');
        current.setAbortAction(true); 
    } else if (validationResult === 'success') {
        current.bid_amount = current.item_name.highest_bid_amount;
    }
})(current, previous);
