// Example of AngularJS routing configuration
var app = angular.module('myApp', ['ngRoute']);

app.config(function($routeProvider) {
  $routeProvider
    .when('/', {
      templateUrl: 'home.html',
      controller: 'HomeController'
    })
    .when('/event-details/:eventId', {
      templateUrl: 'event-details.html',
      controller: 'EventDetailsController'
    })
    .otherwise({ redirectTo: '/' });
});

app.controller('EventDetailsController', function($scope, $routeParams) {
  var eventId = $routeParams.eventId;
  // Use eventId to fetch event details from your backend or service
  // Example: $http.get('/api/events/' + eventId).then(function(response) {
  //   $scope.eventDetails = response.data;
  // });
});
