sp_widget.client_script:
api.controller = function($scope) {
  var c = this;
  c.currentIndex = 0;

  c.next = function() {
    var items = document.querySelectorAll('.carousel-item');
    if (c.currentIndex < items.length - 1) {
      c.currentIndex++;
    } else {
      c.currentIndex = 0;
    }
    updateCarousel();
  };

  c.prev = function() {
    var items = document.querySelectorAll('.carousel-item');
    if (c.currentIndex > 0) {
      c.currentIndex--;
    } else {
      c.currentIndex = items.length - 1;
    }
    updateCarousel();
  };

  function updateCarousel() {
    var carousel = document.querySelector('.carousel');
    var itemWidth = document.querySelector('.carousel-item').offsetWidth;
    var offset = -c.currentIndex * (itemWidth + 20); // Adjust the 20 based on the margin
    carousel.style.transform = 'translateX(' + offset + 'px)';
  }

  c.hover = function(index) {
    var carouselItems = document.querySelectorAll('.carousel-item');
    carouselItems.forEach(function(item, idx) {
      if (idx === index) {
        item.style.transform = 'scale(1.1)';
        item.style.opacity = '1';
        item.style.zIndex = '10';
      } else {
        item.style.transform = 'scale(1)';
        item.style.opacity = '0.8';
        item.style.zIndex = '1';
      }
    });
  };

  c.resetHover = function() {
    var carouselItems = document.querySelectorAll('.carousel-item');
    carouselItems.forEach(function(item) {
      item.style.transform = 'scale(1)';
      item.style.opacity = '0.8';
      item.style.zIndex = '1';
    });
  };
};

