sp_widget.script:
(function() {
  var gr = new GlideRecord('x_1383184_charityb_item');
	gr.addQuery('auction_status','auction_scheduled');// Adjust this if your items table has a different name
  gr.query();
  data.items = [];
  while (gr.next()) {
    data.items.push({
      item_name: gr.getValue('item_name'),
      image: gr.getDisplayValue('image'), // Adjust based on your field for item images
      start_bid_amount: gr.getValue('start_bid_amount') // Assuming 'start_bid_amount' is the field for start bid amount
    });
  }
})();
