sp_widget.client_script:
api.controller = function($http, $location, spUtil, $scope, $timeout) {
    var c = this;

    // Initialize data object for donation form
    c.data = {
        sys_id: $location.search().sys_id,
        itemName: '',
        eventName: '', // Ensure this is correctly fetched from the server
        category: '',
        startDate: '',
        endDate: '',
        value: '',
        startBidAmount: '',
        description: '',
        image: null, // Placeholder for image upload
        errorMessage: '',
        successMessage: '',
        currencyPrefix: '$', // Default to USD ($)
        bidCurrencyPrefix: '$', // Default to USD ($) for start bid amount
        auctionStatus: 'item_submitted'
    };

    // Function to open confirmation modal
    c.openConfirmationModal = function() {
        $('#confirmModal').modal('show'); // Show Bootstrap modal
    };

    // Function to schedule auction
    c.scheduleAuction = function() {
        // Update auction status to 'Auction scheduled'
        var updateData = {
            auction_status: 'auction_scheduled'
        };

        $http.patch('/api/now/table/x_1383184_charityb_item', updateData).then(function(response) {
            if (response.data.result) {
                c.successMessage = 'Auction scheduled successfully!';
                c.data.auctionStatus = 'Auction scheduled'; // Update locally for immediate UI feedback if needed
            } else {
                c.errorMessage = 'Failed to schedule auction.';
            }
        }, function(error) {
            console.error('Error scheduling auction:', error);
            c.errorMessage = 'An error occurred while scheduling auction.';
        });
    };

    // Example function to fetch user's currency preference
    function getUserCurrencyPreference() {
        var userCurrency = 'USD'; // Example: This could come from a service call
        switch (userCurrency) {
            case 'USD':
                c.data.currencyPrefix = '$';
                c.data.bidCurrencyPrefix = '$';
                break;
            case 'INR':
                c.data.currencyPrefix = '₹';
                c.data.bidCurrencyPrefix = '₹';
                break;
            case 'EUR':
                c.data.currencyPrefix = '€';
                c.data.bidCurrencyPrefix = '€';
                break;
            case 'GBP':
                c.data.currencyPrefix = '£';
                c.data.bidCurrencyPrefix = '£';
                break;
            default:
                c.data.currencyPrefix = '$'; // Default to USD ($)
                c.data.bidCurrencyPrefix = '$';
                break;
        }
    }

    // Call the function to set initial currency prefixes
    getUserCurrencyPreference();

    // Fetch event details
    var itemUrl = 'https://dev203498.service-now.com/api/now/table/x_1383184_charityb_events/' + c.data.sys_id;
    $http.get(itemUrl).then(function(response) {
        // Assuming the response.data.result contains the necessary fields
        c.data.eventName = response.data.result.event_name;
        c.data.startDate = response.data.result.start_date;
        c.data.endDate = response.data.result.end_date;
        // Populate other fields as needed

    }, function(error) {
        console.error('Failed to fetch event details:', error);
        c.errorMessage = 'Failed to fetch event details.';
    });

    // Function to calculate minimum start date (today's date)
    c.minStartDate = function() {
        var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth() + 1; // January is 0!
        var yyyy = today.getFullYear();

        if (dd < 10) {
            dd = '0' + dd;
        }

        if (mm < 10) {
            mm = '0' + mm;
        }

        return yyyy + '-' + mm + '-' + dd;
    };

    // Function to submit the donation item
    c.submitDonationItem = function() {
        // Client-side validation
        if (!c.data.itemName || !c.data.eventName || !c.data.category || !c.data.startDate || !c.data.endDate || !c.data.value || !c.data.startBidAmount || !c.data.description) {
            c.errorMessage = 'Please fill in all required fields.';
            return;
        }

        // Validate end date is not before start date
        if (new Date(c.data.endDate) < new Date(c.data.startDate)) {
            c.errorMessage = 'End date cannot be before start date.';
            return;
        }

        // Open confirmation modal
        c.openConfirmationModal();
    };

    // Function to handle donation item submission after confirmation
    // Function to handle donation item submission after confirmation
c.donateItem = function() {
    // Prepare donation item data object
    var donationItemData = {
        item_name: c.data.itemName,
        event_name: c.data.eventName,
        category: c.data.category,
        start_date: c.data.startDate,
        end_date: c.data.endDate,
        value: c.data.value,
        start_bid_amount: c.data.startBidAmount,
        description: c.data.description,
        // Handle image upload separately if required
    };

    // Example: Making a POST request to submit donation item data
    $http.post('/api/now/table/x_1383184_charityb_item', donationItemData).then(function(response) {
        if (response.data.result) {
            c.successMessage = 'Donation item submitted successfully!'; // Set success message
            // Clear form data after successful submission
            c.data = {
                sys_id: c.data.sys_id, // Retain sys_id for potential reuse
                itemName: '',
                eventName: '', // Ensure to reset other fields as needed
                category: '',
                startDate: '',
                endDate: '',
                value: '',
                startBidAmount: '',
                description: '',
                image: null,
                errorMessage: '',
                successMessage: c.successMessage, // Set success message
                currencyPrefix: c.data.currencyPrefix, // Retain currency prefix
                bidCurrencyPrefix: c.data.bidCurrencyPrefix // Retain bid currency prefix
            };
            c.server.refresh(); // Refresh the widget after submission
        } else {
            c.errorMessage = 'Failed to submit donation item.';
        }
    }, function(error) {
        console.error('Error during donation item submission:', error);
        c.errorMessage = 'An error occurred during donation item submission.';
    });

    // Hide modal after submission
    $('#confirmModal').modal('hide');
};

    // Handle the confirm button click within the AngularJS controller
    $scope.$on('modal.confirm', function() {
        c.donateItem();
    });
};

