sp_widget.client_script:
api.controller = function($http, $location, spUtil, $scope) {
    var c = this;

    // Get the item sys_id from the URL
    var itemSysId = $location.search().sys_id;

    // Fetch item details using the item sys_id
    c.data = {};

    // Fetch item details
    $http.get('/api/now/table/x_1383184_charityb_item/' + itemSysId).then(function(response) {
        c.data = response.data.result;
    }, function(error) {
        console.error('Failed to fetch item details:', error);
        c.errorMessage = 'Failed to fetch item details.';
    });

    // Function to submit the bid
    c.submitBid = function() {
        var bidAmount = parseFloat(c.bidAmount);
        if (isNaN(bidAmount)) {
            c.errorMessage = 'Invalid bid amount.';
            return;
        }

        var ga = new GlideAjax('BidValidationAjax');
        ga.addParam('sys_id', c.data.sys_id);
        ga.addParam('bid_amount', bidAmount);

        ga.getXMLAnswer(function(response) {
            var result = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
            if (result.valid) {
                alert('Bid submitted successfully!');
                $location.path('/cb?id=cb_bid_form').search({ sys_id: c.data.sys_id });
            } else {
                c.errorMessage = result.message;
            }
        }, function(error) {
            console.error('Error during bid submission:', error);
            c.errorMessage = 'An error occurred during bid submission.';
        });
    };

    $scope.$on('$locationChangeStart', function(event) {
        if (c.bidAmount) {
            var confirmLeave = confirm('You have unsaved changes. Are you sure you want to leave?');
            if (!confirmLeave) {
                event.preventDefault();
            }
        }
    });
};

