sp_widget.client_script:
api.controller = function($rootScope, $location) {
    var c = this;

    c.showDetails = function(item) {
        var params = {
            sys_id: item.sys_id,
            item_name: item.item_name,  // Adjust based on your item details structure
            // Add other parameters as needed
        };

        // Broadcast event to update cb_bid_form
        $rootScope.$broadcast('sp.update', {
            widget: {
                id: 'cb_bid_form'
            },

            params: params
        });
    };




c.submitBid = function() {
  var bidData = {
    sys_id: c.data.sys_id, // Assuming you've set c.data.sys_id properly in your showDetails function
    item_name: c.data.item_name,
    bid_amount: c.data.bidDetails.bid_amount
  };

  $http.post('/api/submitBid', bidData)
    .then(function(response) {
      console.log('Bid submitted successfully:', response.data);
      // Reset bid form or update UI as needed
    })
    .catch(function(error) {
      console.error('Error submitting bid:', error);
      // Handle error response if needed
    });
};

};

