sp_widget.client_script:
api.controller=(function() {
  var c = this;

  c.data = {
    item: {},
    bid_amount: null
  };

  // Fetch item details
  c.server.get().then(function(response) {
    c.data.item = response.data.item;
  });

  // Function to submit the bid
  c.submitBid = function() {
    c.server.update({
      bid_amount: c.data.bid_amount
    }).then(function(response) {
      if (response.data.successMessage) {
        alert(response.data.successMessage);
        // Optionally refresh item details or perform any other action after bid submission
      } else {
        alert('Failed to place bid: ' + response.data.errorMessage);
      }
    });
  };
})();

