sp_widget.script:
(function($sp, input, data, options, gs) {
  var itemSysId = $sp.getParameter('sys_id');

  if (!itemSysId) {
    data.errorMessage = 'No item specified.';
    return;
  }

  var itemGR = new GlideRecord('x_1383184_charityb_item');
  if (itemGR.get(itemSysId)) {
    data.item = {
      sys_id: itemGR.getUniqueValue(),
      item_name: itemGR.getValue('item_name'),
      start_bid_amount: itemGR.getValue('start_bid_amount'),
      highest_bid_amount: itemGR.getValue('highest_bid_amount')
    };
  } else {
    data.errorMessage = 'Item not found.';
    return;
  }

  // Function to handle bid submission
  data.submitBid = function() {
    var bidGR = new GlideRecord('x_1383184_charityb_bid');
    bidGR.initialize();
    bidGR.setValue('item_name', itemGR.getValue('item_name')); // Set item name
    bidGR.setValue('bid_amount', input.bid_amount);
    //bidGR.setValue('bidder', gs.getUser().getUniqueValue()); // Reference to current user
    if (bidGR.insert()) {
      data.successMessage = 'Bid placed successfully!';
    } else {
      data.errorMessage = 'Failed to place bid.';
    }
  };
})($sp, input, data, options, gs);
