sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	$scope.submitForm = function() {
		var startDateTimeUTC = convertToUTC($scope.startDateTime);
		var endDateTimeUTC = convertToUTC($scope.endDateTime);
		var formData = {
			workout:selected_workoutID,
			start_date:startDateTimeUTC,
			end_date:endDateTimeUTC,
		};
		console.log(formData )

		$http({
			method: 'POST',
			url: '/api/now/table/x_1383283_fitness_proworkout', 
			data: formData
		}).then(function(response) {
			$location.search({id: 'fn_my_workouts'});
			console.log('Form submitted successfully:', response.data);

			$scope.selectedWorkout = '';
			$scope.startDateTime = '';
			$scope.endDateTime = '';
		}, function(error) {

			console.error('Error submitting form:', error);
		});
	};
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


var WorkoutGR=new GlideRecord('x_1383283_fitness_workout');
	//WorkoutGR.addQuery('name');
	WorkoutGR.query();
	while(WorkoutGR.next()){
				data.allworkout.push({
					name:WorkoutGR.getDisplayValue('name'),
					sys_id: WorkoutGR.sys_id.toString()

				});

				}
		console.log(data.allworkout);
})();
