sp_widget.client_script:
api.controller=function($scope, $http) {
    $scope.event = {
        title: '',
        location: '',
        type: 'private',
        event_manager: '',
        charityCompany: '',
        startDate: '',
        endDate: '',
        time: '',
        status: 'upcoming',
        description: '',
        justGivingUrl: '',
        imageUrl: ''
    };

    $scope.createEvent = function() {
        var formData = new FormData();
        formData.append('title', $scope.event.title);
        formData.append('location', $scope.event.location);
        formData.append('type', $scope.event.type);
        formData.append('event_manager', $scope.event_manager);
        formData.append('charityCompany', $scope.event.charity_company);
        formData.append('startDate', $scope.event.startDate);
        formData.append('endDate', $scope.event.endDate);
      //  formData.append('time', $scope.event.time);
        formData.append('status', $scope.event.status);
        formData.append('description', $scope.event.description);
        formData.append('justGivingUrl', $scope.event.justGivingUrl);
        formData.append('image', $scope.event.image);

        $http.post('/api/now/table/x_1383184_charityb_events', formData, {
            headers: { 'Content-Type': undefined }
        }).then(function(response) {
            $scope.eventCreated = true;
            $scope.createdEvent = response.data.result;
        }, function(error) {
            console.error('Error creating event', error);
        });
    };
}

