sp_widget.script:
(function() {
    data.createdEvent = null;

    if (input) {
        var eventGR = new GlideRecord('x_1383184_charityb_events');
        eventGR.initialize();


        eventGR.setValue('event_manager', input.event_manager);
        eventGR.setValue('charity_company', input.charityCompany);
        eventGR.setValue('start_date', input.startDate);
        eventGR.setValue('end_date', input.endDate);

        eventGR.setValue('status', input.status);
        eventGR.setValue('description', input.description);
        eventGR.setValue('just_giving_url', input.justGivingUrl);
        eventGR.setValue('image', input.image); // Assuming you handle image upload separately
        eventGR.insert();

        data.createdEvent = {
            //title: eventGR.getValue('title'),
            //location: eventGR.getValue('location'),
          //  type: eventGR.getValue('type'),
            event_manager: eventGR.getValue('event_manager'),
            charityCompany: eventGR.getValue('charity_company'),
            startDate: eventGR.getValue('start_date'),
            endDate: eventGR.getValue('end_date'),
           // time: eventGR.getValue('time'),
            status: eventGR.getValue('event_status'),
            description: eventGR.getValue('description'),
            justGivingUrl: eventGR.getValue('just_giving_url'),
            image: eventGR.getValue('image')
        };
    }
})();
