sp_widget.script:
(function() {
    if (input && input.action === "update_session_tracking_info") {
        gs.getSession().putProperty("usage_tracking_allowed_for_session", input.user_tracking);
        return;
    }

    data.sysUserID = $sp.getParameter("sys_id");
    if (!data.sysUserID) {
        data.sysUserID = gs.getUser().getID();
    }

    var userProfileGR = new GlideRecord("x_1383184_charityb_user_profile");
    data.userExists = userProfileGR.get(data.sysUserID) && userProfileGR.canRead();

    if (data.userExists) {
        data.name = userProfileGR.getValue("name");
        data.location = userProfileGR.getValue("location");
        data.bio = userProfileGR.getValue("bio");
        data.profile_image = userProfileGR.getValue("profile_image");

        data.isLoggedInUsersProfile = gs.getUser().getID() == data.sysUserID;

        var userProfileForm = $sp.getForm("x_1383184_charityb_user_profile", data.sysUserID);
        data.sysUserView = userProfileForm._view;
        data.sysUserModel = userProfileForm._fields;
        data.sysUserModelList = [];

        for (var i = 0; i < data.sysUserView.length; i++) {
            data.sysUserModelList.push(data.sysUserModel[data.sysUserView[i].name]);
        }
    }
})();
