sp_widget.client_script:
api.controller = function($scope) {
    var c = this;

    // Initialize data.items
    c.data.events = c.data.events || [];

    // Function to handle placing a bid
   // c.placeBid = function(item) {
       // alert('Placing bid for: ' + item.item_name);
        // Add your logic here to open a bid modal, send a request, etc.
   // };

    // Function to show item details
    c.showDetails = function(event) {
        var eventDetailUrl = "?id=event_details&sys_id=" + event.sys_id;
        window.location.href = eventDetailUrl;
    };

    // Watch the items array for changes
    $scope.$watch(function() {
        return c.data.events;
    }, function(newVal, oldVal) {
        if (newVal && newVal.length === 0) {
            $scope.noEventsMessage = "No events available";
        } else {
            $scope.noEventsMessage = "";
        }
    }, true);
};

