sp_widget.script:
(function($sp, input, data, options, gs) {
    // Initialize data arrays
    data.events = [];

    // Fetch all items in 'auction_in_progress' status
    var eventGR = new GlideRecord('x_1383184_charityb_events');
    eventGR.addQuery('event_status', 'schedule');



    eventGR.query();
    while (eventGR.next()) {
        var event_managerName = '';
       if (eventGR.event_manager) {
           var userGR = new GlideRecord('sys_user');
            if (userGR.get(eventGR.event_manager)) {
                event_managerName = userGR.getValue('name');
            }
       }

        data.events.push({
            sys_id: eventGR.getUniqueValue(),
            event_name: eventGR.getValue('event_name'),
            //donator: donatorName,
            description: eventGR.getValue('description'),
           //end_date: itemGR.getValue('end_date'),
            //value: itemGR.getValue('value'),
            //start_bid_amount: itemGR.getValue('start_bid_amount'),
            //highest_bid_amount: itemGR.getValue('highest_bid_amount'),
           // auction_status: itemGR.getValue('auction_status'),
           image: eventGR.getDisplayValue('image'),
           // description: itemGR.getValue('description')
        });
    }

    if (data.events.length === 0) {
        gs.addInfoMessage("No Events Available");
    }
})($sp, input, data, options, gs);
