sp_widget.client_script:
api.controller=function($scope, spUtil) {
    var c = this;

    c.data.eventDetails = {
        imageUrl: '',
        name: '',
        charityCompany: '',
        eventManager: '',
        startDate: '',
        endDate: '',
        description: '',
        donateUrl: '',
        testimonials: ''
    };

    // Function to load event details
    c.loadEventDetails = function() {
        // Check if the event_sys_id is provided
        if (c.options.event_sys_id) {
            var params = {
                sys_id: c.options.sys_id
            };

            // Use spUtil to get data from the server
            spUtil.get('your_widget_id', params).then(function(response) {
                if (response.data && response.data.eventDetails) {
                    c.data.eventDetails = response.data.eventDetails;
                }
            });
        }
    };

    // Call the function to load event details when the controller is initialized
    c.loadEventDetails();
}

