sp_widget.script:
(function() {
    data.eventDetails = {};

    // sys_id of event
    var eventSysId = options.sys_id || input.sys_id || $sp.getParameter("sys_id");

    if (eventSysId) {
        var gr = new GlideRecord('x_1383184_charityb_events');
        if (gr.get(eventSysId)) {
            data.eventDetails = {
                imageUrl: gr.getValue('image'),
                name: gr.getValue('event_name'),
                charityCompany: gr.getDisplayValue('charity_company'),  
                eventManager: gr.getDisplayValue('event_manager'),  
                startDate: gr.getValue('start_date'),
                endDate: gr.getValue('end_date'),
                description: gr.getValue('description'),
                donateUrl: gr.getValue('just_giving_url'),
                testimonials: gr.getValue('testimonials')
            };
        }
    }
})();
