sp_widget.script:
(function($sp, input, data, options, gs) {
    data.events = [];

    // Fetch events from ServiceNow table
    var eventGR = new GlideRecord('x_1383184_charityb_events');
    eventGR.addQuery('event_status', 'IN', ['in progress', 'schedule', 'completed']);
    eventGR.query();

    while (eventGR.next()) {
        var event_managerName = '';
        if (eventGR.event_manager) {
            var userGR = new GlideRecord('x_1383184_charityb_user_profile');
            if (userGR.get(eventGR.event_manager)) {
                event_managerName = userGR.getValue('name');
            }
        }

        data.events.push({
            sys_id: eventGR.getUniqueValue(),
            event_name: eventGR.getValue('event_name'),
            description: eventGR.getValue('description'),
            start_date: eventGR.getValue('start_date'),
            end_date: eventGR.getValue('end_date'),
            charity_company: eventGR.getDisplayValue('charity_company'),
            status: eventGR.getValue('event_status'),
            image: eventGR.getDisplayValue('image'),
            justgiving_url: eventGR.getValue('justgiving_url'),
        });
    }

    // Fetch event types from JustGiving API
    try {
        var request = new sn_ws.RESTMessageV2();
        request.setHttpMethod("GET");
        request.setEndpoint("https://api.justgiving.com/c89bbb1c/v1/event/types");
        request.setRequestHeader("Content-Type", "application/xml");

        var response = request.execute();
        var responseBody = response.getBody();

        // Parse the XML response using GlideXMLDocument
        var xmlDoc = new GlideXMLDocument(responseBody);
        var eventTypes = xmlDoc.getElementsByTagName("eventTypes");

        // Process and add JustGiving events
        for (var i = 0; i < eventTypes.getLength(); i++) {
            var justGivingEvent = eventTypes.item(i);

            data.events.push({
                sys_id: justGivingEvent.getAttribute("id"),
                event_name: justGivingEvent.getAttribute("name"),
                description: justGivingEvent.getAttribute("description"),
                start_date: '', // JustGiving event types don't have start_date
                end_date: '', // JustGiving event types don't have end_date
                charity_company: '', // JustGiving event types don't have charity_company
                status: '', // JustGiving event types don't have status
                image: '', // JustGiving event types don't have image
                justgiving_url: '', // JustGiving event types don't have URL
            });
        }
    } catch (e) {
        gs.addErrorMessage("Error fetching events from JustGiving: " + e.message);
    }

    if (data.events.length === 0) {
        gs.addInfoMessage("No Events Available");
    }
})($sp, input, data, options, gs);
