sp_widget.client_script:
api.controller = function($scope) {
    var c = this;

    // Function to handle category selection
    c.selectCategory = function(categoryName) {
        window.location.href = 'cb?id=cb_test&category=' + encodeURIComponent(categoryName);
    };


};

