(function executeRule(current, previous /*null when async*/) {
    var bidAmount = parseFloat(current.getValue('bid_amount'));
    var itemSysId = current.getValue('item_name');

    var bidValidation = new BidValidation();
    var result = bidValidation.validateBidAmount(bidAmount, itemSysId);

    if (!result.valid) {
        gs.addErrorMessage(result.message);
        current.setAbortAction(true); // Prevent the insert operation
    } else if (result.highestBidUpdated) {
        gs.addInfoMessage(gs.getMessage('Highest bid amount updated successfully.'));
    }
})(current, previous);
