sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
	}
    var itemNameField = g_form.getReference('item_name', displayBidValidationErrors);

    function displayBidValidationErrors(referenceField) {
        if (!referenceField) {
            return;
        }

        var bidAmountField = g_form.getControl('bid_amount');
        bidAmountField.observe('change', function() {
            var itemName = g_form.getValue('item_name');
            var bidAmount = parseFloat(g_form.getValue('bid_amount'));

            // AJAX call to validate bid amount
            var ga = new GlideAjax('BidValidationUtil');
            ga.addParam('sysparm_name', 'validateBidAmount');
            ga.addParam('itemName', itemName);
            ga.addParam('bidAmount', bidAmount);
            ga.getXMLAnswer(function(response) {
                if (response === 'errorLessThanHighest') {
                    g_form.addErrorMessage('Bid amount must be greater than the current highest bid amount.');
                } else if (response === 'errorLessThanStart') {
                    g_form.addErrorMessage('Bid amount must be greater than the start bid amount.');
                } else if (response === 'errorItemNotFound') {
                    g_form.addErrorMessage('Item not found with name: ' + itemName);
                }
            });
        });
    }
