sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var isNewRecord = g_form.isNewRecord();

    var startDate = g_form.getValue('start_date');
    var currentDate = new Date();

    var startDateObj = new Date(startDate);

    if (startDateObj.toDateString() === currentDate.toDateString()) {
        g_form.clearValue('start_date');
        g_form.showFieldMsg('start_date', getMessage('start_date_today'), 'error');
        return;
    } else if (isNewRecord && startDateObj < currentDate) {
        g_form.clearValue('start_date');
        g_form.showFieldMsg('start_date', getMessage('start_date_before_today'), 'error');
        return;
    }

    g_form.clearMessages('start_date');
}

function getMessage(key) {
    var messages = {};
    messages['start_date_today'] = 'Start date cannot be today';
    messages['start_date_before_today'] = 'Start date cannot be before today';
    return messages[key];
}
