sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Get the user profile associated with the donator
    var userProfile = new GlideRecord('x_1383184_charityb_user_profile');
    if (userProfile.get(current.name)) {

        // Count the number of donations by the donator
        var donationCount = new GlideAggregate('x_1383184_charityb_item');
        donationCount.addQuery('donator', current.name);
        donationCount.addQuery('auction_status', 'auction_scheduled');
        donationCount.addAggregate('COUNT');
        donationCount.query();

        var donationNumber = 0;
        if (donationCount.next()) {
            donationNumber = donationCount.getAggregate('COUNT');
        }

        // Determine which badge to award based on the number of donations
        var badgeName;
        if (donationNumber == 1) {
            badgeName = 'Badge1';
        } else if (donationNumber == 3) {
            badgeName = 'Badge2';
        } else if (donationNumber == 5) {
            badgeName = 'Badge3';
        } else if (donationNumber == 7) {
            badgeName = 'Badge4';
        } else if (donationNumber > 10) {
            badgeName = 'Badge5';
        }

        if (badgeName) {
            // Fetch the badge from the badges table
            var badge = new GlideRecord('x_1383184_charityb_badges');
            badge.addQuery('badge_name', badgeName);
            badge.query();

            if (badge.next()) {
                // Add the badge to the user's profile if not already present
                var userBadges = userProfile.badges.split(',');
                if (userBadges.indexOf(badge.sys_id.toString()) == -1) {
                    userBadges.push(badge.sys_id.toString());
                    userProfile.badges = userBadges.join(',');
                    userProfile.update();
                }
            }
        }
    }
})(current, previous);
