sys_script_include.script:
var BidValidation = Class.create();
BidValidation.prototype = {
    initialize: function() {},

    validateBidAmount: function(bidAmount, itemSysId) {
        var result = {
            valid: false,
            message: '',
            highestBidUpdated: false
        };

        var item = new GlideRecord('x_1383184_charityb_item');
        if (item.get(itemSysId)) {
            var startBidAmount = parseFloat(item.getValue('start_bid_amount'));
            var highestBidAmount = parseFloat(item.getValue('highest_bid_amount'));

            if (bidAmount <= startBidAmount) {
                result.message = gs.getMessage('bid_amount_greater_start');
            } else if (bidAmount <= highestBidAmount) {
                result.message = gs.getMessage('bid_amount_greater_highest');
            } else {
                result.valid = true;
                item.setValue('highest_bid_amount', bidAmount);
                item.update();
                result.highestBidUpdated = true;
            }
        } else {
            result.message = gs.getMessage('item_not_found');
        }

        return result;
    },

    type: 'BidValidation'
};
