sys_script_include.script:
var UserProfileUtils = Class.create();
UserProfileUtils.prototype = {
    initialize: function() {},


    getUserProfileSysId: function() {
        var userSysId = gs.getUserID();
        var userProfileSysId = '';


        var userProfile = new GlideRecord('x_1383184_charityb_user_profile');
        userProfile.addQuery('name', userSysId);//name is reference field to sys_user
        userProfile.query();
        if (userProfile.next()) {
            userProfileSysId = userProfile.sys_id.toString();
        }

        return userProfileSysId;
    },


    getCurrentUserLocation: function() {
        var user = new GlideRecord('sys_user');
        if (user.get(gs.getUserID())) {
            return user.location;
        }
        return '';
    },

    type: 'UserProfileUtils'
};
