sys_ui_action.script:
function assignToMeAndCheckIssues() {
    var issueSysID = g_form.getUniqueValue(); // Get the Sys ID of the current issue record
    var itemSysID = g_form.getValue('u_item'); // Replace 'u_item' with your actual item reference field
    var event = g_form.getValue('u_event'); // Replace 'u_event' with your actual event field

    // Perform the assignment
    g_form.setValue('assigned_to', gs.getUserID());

    // Query to count remaining issues for the same item and event
    var issueCount = new GlideAggregate('x_1383184_charityb_issues');
    issueCount.addQuery('item_name', itemSysID);
    issueCount.addQuery('event_type', event);
    issueCount.addQuery('active', true);
    issueCount.addEncodedQuery('state!=7'); // Exclude closed issues
    issueCount.addAggregate('COUNT');
    issueCount.query();

    var remainingIssues = 0;
    if (issueCount.next()) {
        remainingIssues = issueCount.getAggregate('COUNT');
    }

    // Display pop-up message with remaining issues count
    alert('There are ' + remainingIssues + ' remaining issues for this item and event.');
}
