sysauto_script.script:
(function() {
    var auctionItemGr = new GlideRecord('x_1383184_charityb_item');
    auctionItemGr.query();

    while (auctionItemGr.next()) {
        var startDate = new GlideDateTime(auctionItemGr.start_date);
        var endDate = new GlideDateTime(auctionItemGr.end_date);
        var currentDate = new GlideDateTime();

        if (currentDate.compareTo(startDate) >= 0 && currentDate.compareTo(endDate) < 0) {
            auctionItemGr.auction_status = 'auction_in_progress';
        } else if (currentDate.compareTo(endDate) >= 0) {
            auctionItemGr.auction_status = 'auction_completed';
        }

        auctionItemGr.update();
    }
})();
